
SELECT MAX("batting"."HBP") as agg_0 FROM "batting" LEFT OUTER JOIN "players" ON "batting"."playerID" = "players"."playerID" LEFT OUTER JOIN "teams" ON "batting"."teamID" = "teams"."teamID" LEFT OUTER JOIN "salaries" ON "teams"."teamID" = "salaries"."teamID" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner"  WHERE "salaries"."lgID" IN ('AL') AND "batting"."stint" = 1 AND "salaries"."playerID" IN ('bondsba01', 'johnsra05', 'thomeji01', 'gordoto01', 'griffke02', 'moyerja01', 'francjo01') AND ("players"."nameLast" != '%Spring%' OR "players"."nameLast" = '%Lundbom%' OR "players"."nameLast" = '%Conzelman%') AND "seriespost"."teamIDwinner" IS NOT NULL;