
SELECT SUM("fieldingpost"."PB") as agg_0 FROM "pitching" LEFT OUTER JOIN "players" ON "pitching"."playerID" = "players"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID" LEFT OUTER JOIN "fielding" ON "players"."playerID" = "fielding"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID" LEFT OUTER JOIN "schools" ON "schoolsplayers"."schoolID" = "schools"."schoolID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID"  WHERE "schools"."schoolCity" LIKE '%S%an%' AND "players"."weight" IS NOT NULL AND "pitching"."lgID" != '%AA%' AND "awardsshareplayers"."lgID" != '%NL%' AND "appearances"."G_1b" <= 1 AND "players"."deathCity" IN ('Los Angeles', 'New York', 'Cincinnati', 'San Francisco') AND "fieldingof"."playerID" IS NOT NULL AND "fielding"."playerID" IS NOT NULL;