
SELECT SUM("players"."weight") as agg_0 FROM "appearances" LEFT OUTER JOIN "players" ON "appearances"."playerID" = "players"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "schools" ON "schoolsplayers"."schoolID" = "schools"."schoolID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID"  WHERE "players"."deathCity" IN ('Los Angeles', 'Chicago', 'Philadelphia', 'San Francisco', 'Brooklyn', 'New York', 'Baltimore', 'Cincinnati') AND "schools"."schoolState" IN ('FL', 'NY', 'VA', 'PA', 'IL', 'NC') AND "batting"."yearID" BETWEEN 1904 AND 1995 AND "players"."birthCountry" IS NOT NULL AND "appearances"."teamID" = '%CL6%' AND "awardsplayers"."playerID" IS NOT NULL AND "pitchingpost"."playerID" IS NOT NULL AND "halloffame"."hofID" IS NOT NULL AND "fieldingof"."playerID" IS NOT NULL AND "awardsshareplayers"."playerID" IS NOT NULL AND "battingpost"."playerID" IS NOT NULL;