
SELECT MAX("batting"."SH") as agg_0 FROM "awardsshareplayers" LEFT OUTER JOIN "players" ON "awardsshareplayers"."playerID" = "players"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID" LEFT OUTER JOIN "fielding" ON "players"."playerID" = "fielding"."playerID" LEFT OUTER JOIN "teams" ON "batting"."teamID" = "teams"."teamID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID"  WHERE "awardsplayers"."awardID" != '%Baseball Magazine All-Star%' AND "awardsshareplayers"."awardID" NOT LIKE '%Year%' AND "players"."birthState" != '%OR%' AND "players"."bats" IS NOT NULL AND ("fielding"."teamID" = '%SE1%' OR ("fielding"."teamID" IN ('NYA') AND "fielding"."teamID" NOT LIKE '%LAN%')) AND "teams"."teamID" IS NOT NULL AND "halloffame"."hofID" IS NOT NULL AND "pitchingpost"."playerID" IS NOT NULL;