
SELECT COUNT(*) as agg_0 FROM "managers" LEFT OUTER JOIN "teams" ON "managers"."teamID" = "teams"."teamID" LEFT OUTER JOIN "awardsmanagers" ON "managers"."managerID" = "awardsmanagers"."managerID" LEFT OUTER JOIN "teamshalf" ON "teams"."teamID" = "teamshalf"."teamID" LEFT OUTER JOIN "salaries" ON "teams"."teamID" = "salaries"."teamID" LEFT OUTER JOIN "awardssharemanagers" ON "managers"."managerID" = "awardssharemanagers"."managerID" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner" LEFT OUTER JOIN "els_teamnames" ON "teams"."teamID" = "els_teamnames"."teamid" LEFT OUTER JOIN "managershalf" ON "managers"."managerID" = "managershalf"."managerID"  WHERE "seriespost"."lgIDloser" IN ('NL') AND "awardssharemanagers"."lgID" NOT LIKE '%N%L%' AND ("teamshalf"."teamID" IN ('CIN', 'SFN', 'LAN', 'SLN', 'SEA', 'TOR', 'CLE', 'TEX', 'ML4') OR "teamshalf"."Half" >= 1) AND "salaries"."teamID" NOT LIKE '%N%YA%' AND "awardsmanagers"."awardID" = '%TSN Manager of the Year%' AND ("awardsmanagers"."managerID" LIKE '%robinfr02m%' OR ("awardsmanagers"."lgID" = '%NL%' AND "awardsmanagers"."managerID" LIKE '%freyji99m%')) AND "els_teamnames"."teamid" IS NOT NULL AND "managershalf"."managerID" IS NOT NULL;