
SELECT MAX("battingpost"."SO") as agg_0 FROM "pitching" LEFT OUTER JOIN "players" ON "pitching"."playerID" = "players"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID"  WHERE "schoolsplayers"."schoolID" NOT LIKE '%s%tanford%' AND "schoolsplayers"."schoolID" = '%wlmcarey%' AND "awardsplayers"."awardID" IN ('Cy Young Award', 'TSN Pitcher of the Year', 'TSN Fireman of the Year', 'TSN Player of the Year') AND "awardsplayers"."notes" LIKE '%3B%' AND "appearances"."playerID" IS NOT NULL AND "awardsshareplayers"."playerID" IS NOT NULL AND "fieldingpost"."playerID" IS NOT NULL;