
SELECT SUM("teams"."FP") as agg_0 FROM "teams" LEFT OUTER JOIN "els_teamnames" ON "teams"."teamID" = "els_teamnames"."teamid" LEFT OUTER JOIN "managers" ON "teams"."teamID" = "managers"."teamID" LEFT OUTER JOIN "allstarfull" ON "teams"."teamID" = "allstarfull"."teamID" LEFT OUTER JOIN "teamsfranchises" ON "els_teamnames"."franchid" = "teamsfranchises"."franchID" LEFT OUTER JOIN "batting" ON "teams"."teamID" = "batting"."teamID"  WHERE "batting"."lgID" NOT LIKE '%AL%' AND "els_teamnames"."name" IN ('Washington Nationals', 'Baltimore Orioles', 'New York Giants', 'Chicago White Stockings', 'Pittsburgh Pirates', 'Philadelphia Phillies', 'Washington Senators', 'Cincinnati Reds') AND "allstarfull"."lgID" LIKE '%N%L%' AND "batting"."teamID" NOT LIKE '%NY%N%' AND "managers"."teamID" IS NOT NULL AND "teamsfranchises"."franchID" IS NOT NULL;