
SELECT SUM("fieldingof"."stint") as agg_0 FROM "awardsshareplayers" LEFT OUTER JOIN "players" ON "awardsshareplayers"."playerID" = "players"."playerID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID"  WHERE "awardsshareplayers"."lgID" IN ('AL', 'NL') AND "fieldingof"."Gcf" <= 0.088262501848137 AND "awardsshareplayers"."awardID" NOT LIKE '%MV%P%' AND "players"."deathCity" = '%Olyphant%' AND "players"."birthState" LIKE '%M%D%' AND ("players"."birthState" LIKE '%AL%' OR "players"."birthState" LIKE '%PA%') AND "players"."deathState" LIKE '%IL%' AND "players"."nameFirst" LIKE '%B%ill%';