
SELECT MAX("pitching"."GS") as agg_0 FROM "awardsplayers" LEFT OUTER JOIN "players" ON "awardsplayers"."playerID" = "players"."playerID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "schools" ON "schoolsplayers"."schoolID" = "schools"."schoolID" LEFT OUTER JOIN "pitching" ON "players"."playerID" = "pitching"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID"  WHERE "schoolsplayers"."schoolID" NOT LIKE '%notr%edame%' AND ("battingpost"."lgID" IN ('NL', 'AA') OR "battingpost"."GIDP" >= 0.0 OR "battingpost"."lgID" IN ('AL')) AND "players"."deathCountry" IN ('France', 'D.R.', 'Venezuela', 'Bahamas', 'P.R.', 'Cuba', 'Philippines') AND "schoolsplayers"."playerID" IN ('daniebe01', 'dowdto01', 'brownbo03', 'johnsfr02') AND "schools"."schoolNick" IN ('Pioneers', 'Rams', 'Wildcats', 'Tigers', 'Panthers') AND "pitching"."SO" >= 36 AND "pitchingpost"."playerID" IS NOT NULL AND "fieldingpost"."playerID" IS NOT NULL AND "halloffame"."hofID" IS NOT NULL;