
SELECT MIN("schoolsplayers"."yearMax") as agg_0 FROM "schools" LEFT OUTER JOIN "schoolsplayers" ON "schools"."schoolID" = "schoolsplayers"."schoolID" LEFT OUTER JOIN "players" ON "schoolsplayers"."playerID" = "players"."playerID" LEFT OUTER JOIN "fielding" ON "players"."playerID" = "fielding"."playerID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID"  WHERE "schoolsplayers"."yearMin" <= 1916 AND "schools"."schoolName" LIKE '%N%ew%' AND "fieldingof"."playerID" != '%pickuty01%' AND "fielding"."playerID" IS NOT NULL AND "battingpost"."playerID" IS NOT NULL;