
SELECT COUNT(*) as agg_0 FROM "players" LEFT OUTER JOIN "pitching" ON "players"."playerID" = "pitching"."playerID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "fielding" ON "players"."playerID" = "fielding"."playerID"  WHERE ("schoolsplayers"."playerID" = '%bakerda01%' OR "schoolsplayers"."playerID" != '%farrji01%') AND "pitching"."CG" >= 0 AND "pitching"."HBP" IS NOT NULL AND "fieldingof"."Gcf" <= 0.0 AND "fielding"."POS" IN ('1B', '2B', 'P', 'C', 'RF', 'OF', '3B', 'SS', 'LF') AND "fieldingof"."yearID" >= 1894;