
SELECT COUNT(*) as agg_0 FROM "awardsplayers" LEFT OUTER JOIN "players" ON "awardsplayers"."playerID" = "players"."playerID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID"  WHERE "pitchingpost"."teamID" LIKE '%CH%N%' AND ("awardsplayers"."lgID" IN ('AL') OR "awardsplayers"."playerID" IN ('bondsba01', 'robinbr01', 'dimagjo01')) AND "players"."birthCountry" IN ('Mexico', 'CAN', 'Cuba', 'P.R.', 'Venezuela', 'Ireland', 'USA', 'D.R.') AND "awardsshareplayers"."lgID" NOT LIKE '%A%L%' AND "players"."deathState" LIKE '%PA%' AND "fieldingpost"."teamID" NOT LIKE '%BR%O%' AND "players"."deathCity" LIKE '%N%ew%' AND "awardsshareplayers"."pointsWon" BETWEEN 22 AND 124;