
SELECT SUM("appearances"."G_all") as agg_0 FROM "pitchingpost" LEFT OUTER JOIN "players" ON "pitchingpost"."playerID" = "players"."playerID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID"  WHERE "appearances"."lgID" IN ('AA', 'FL', 'AL', 'UA', 'PL') AND "awardsshareplayers"."awardID" = '%MVP%' AND "awardsshareplayers"."lgID" NOT LIKE '%NL%' AND "pitchingpost"."teamID" LIKE '%S%EA%' AND "fieldingpost"."POS" LIKE '%2%B%' AND ("fieldingpost"."PO" BETWEEN 0 AND 1 OR "fieldingpost"."lgID" NOT LIKE '%N%L%' OR "fieldingpost"."PO" <= 5) AND "appearances"."G_2b" >= 5 AND "awardsplayers"."playerID" IS NOT NULL AND "fieldingof"."playerID" IS NOT NULL AND "halloffame"."hofID" IS NOT NULL;