
SELECT AVG("awardsmanagers"."yearID") as agg_0 FROM "awardssharemanagers" LEFT OUTER JOIN "managers" ON "awardssharemanagers"."managerID" = "managers"."managerID" LEFT OUTER JOIN "managershalf" ON "managers"."managerID" = "managershalf"."managerID" LEFT OUTER JOIN "awardsmanagers" ON "managers"."managerID" = "awardsmanagers"."managerID" LEFT OUTER JOIN "teams" ON "managers"."teamID" = "teams"."teamID" LEFT OUTER JOIN "allstarfull" ON "teams"."teamID" = "allstarfull"."teamID"  WHERE "allstarfull"."gameID" != '%NLS200907140%' AND "awardsmanagers"."tie" IS NOT NULL AND "awardsmanagers"."managerID" = '%johnsda01m%' AND ("teams"."teamID" IN ('CHN', 'SLN', 'CIN', 'CLE', 'PIT', 'DET', 'CHA', 'PHI', 'NYA') OR "teams"."teamID" IN ('SLN') OR "teams"."teamID" NOT LIKE '%CHN%') AND "managershalf"."managerID" IS NOT NULL;