
SELECT MIN("players"."weight") as agg_0 FROM "schools" LEFT OUTER JOIN "schoolsplayers" ON "schools"."schoolID" = "schoolsplayers"."schoolID" LEFT OUTER JOIN "players" ON "schoolsplayers"."playerID" = "players"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID"  WHERE "schoolsplayers"."schoolID" = '%chapmanca%' AND "awardsplayers"."lgID" IN ('AA', 'NL', 'ML') AND ("schools"."schoolName" NOT LIKE '%Ci%ty%' OR "schools"."schoolCity" NOT LIKE '%San%') AND "appearances"."G_1b" BETWEEN 0 AND 1 AND "players"."nameNick" = '%Baby Manny,Big Vic%' AND "awardsshareplayers"."awardID" NOT LIKE '%of%' AND "appearances"."G_of" >= 0 AND "schools"."schoolID" != '%tennessee%' AND "halloffame"."hofID" IS NOT NULL;