
SELECT MIN("fielding"."CS") as agg_0 FROM "fieldingof" LEFT OUTER JOIN "players" ON "fieldingof"."playerID" = "players"."playerID" LEFT OUTER JOIN "fielding" ON "players"."playerID" = "fielding"."playerID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID" LEFT OUTER JOIN "teams" ON "batting"."teamID" = "teams"."teamID" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner"  WHERE ("teams"."lgID" NOT LIKE '%N%L%' OR "teams"."lgID" IN ('NL', 'UA', 'FL', 'AL') OR "teams"."lgID" LIKE '%A%L%') AND "players"."birthState" IS NOT NULL AND "awardsshareplayers"."lgID" != '%AL%' AND "pitchingpost"."lgID" NOT LIKE '%AL%' AND ("seriespost"."lgIDloser" = '%AL%' OR "seriespost"."ties" != 0) AND "appearances"."playerID" IS NOT NULL AND "fieldingpost"."playerID" IS NOT NULL;