
SELECT MIN("teams"."RA") as agg_0 FROM "managers" LEFT OUTER JOIN "awardssharemanagers" ON "managers"."managerID" = "awardssharemanagers"."managerID" LEFT OUTER JOIN "awardsmanagers" ON "managers"."managerID" = "awardsmanagers"."managerID" LEFT OUTER JOIN "managershalf" ON "managers"."managerID" = "managershalf"."managerID" LEFT OUTER JOIN "teams" ON "managers"."teamID" = "teams"."teamID"  WHERE "teams"."WSWin" IN ('N') AND "awardsmanagers"."managerID" != '%freyji99m%' AND "awardssharemanagers"."awardID" = '%Mgr of the Year%' AND ("managershalf"."teamID" NOT LIKE '%LS3%' OR ("managershalf"."half" BETWEEN 1 AND 2 AND "managershalf"."teamID" LIKE '%C%AL%')) AND ("awardssharemanagers"."votesFirst" <= 0 OR "awardssharemanagers"."managerID" != '%mathemi01m%' OR "awardssharemanagers"."votesFirst" >= 0) AND "awardssharemanagers"."pointsWon" <= 149;