
SELECT MIN("battingpost"."BB") as agg_0 FROM "fieldingpost" LEFT OUTER JOIN "players" ON "fieldingpost"."playerID" = "players"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID" LEFT OUTER JOIN "pitching" ON "players"."playerID" = "pitching"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "fielding" ON "players"."playerID" = "fielding"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID"  WHERE ("pitching"."BB" >= 38 OR "pitching"."BB" <= 42) AND "battingpost"."lgID" NOT LIKE '%A%L%' AND "awardsplayers"."notes" LIKE '%LF%' AND "halloffame"."category" NOT LIKE '%Pioneer/Exec%utive%' AND "halloffame"."category" NOT LIKE '%Player%' AND ("schoolsplayers"."schoolID" IN ('arizonast', 'texas', 'stanford', 'stmarysca', 'michigan', 'illinois') OR "schoolsplayers"."schoolID" NOT LIKE '%texa%s%') AND "halloffame"."inducted" != '%Y%' AND "fielding"."playerID" IS NOT NULL AND "batting"."playerID" IS NOT NULL AND "fieldingof"."playerID" IS NOT NULL AND "appearances"."playerID" IS NOT NULL;