
SELECT MAX("managers"."inseason") as agg_0 FROM "salaries" LEFT OUTER JOIN "teams" ON "salaries"."teamID" = "teams"."teamID" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner" LEFT OUTER JOIN "managers" ON "teams"."teamID" = "managers"."teamID" LEFT OUTER JOIN "els_teamnames" ON "teams"."teamID" = "els_teamnames"."teamid" LEFT OUTER JOIN "awardsmanagers" ON "managers"."managerID" = "awardsmanagers"."managerID" LEFT OUTER JOIN "allstarfull" ON "teams"."teamID" = "allstarfull"."teamID"  WHERE "seriespost"."teamIDloser" IN ('DET', 'NY1', 'PHI', 'NYA', 'SLN') AND "seriespost"."wins" <= 3 AND "teams"."FP" = 0 AND "managers"."L" >= 60 AND "els_teamnames"."teamid" IS NOT NULL AND "awardsmanagers"."managerID" IS NOT NULL AND "allstarfull"."teamID" IS NOT NULL;