
SELECT MIN("appearances"."yearID") as agg_0 FROM "fieldingpost" LEFT OUTER JOIN "players" ON "fieldingpost"."playerID" = "players"."playerID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "fielding" ON "players"."playerID" = "fielding"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID"  WHERE "pitchingpost"."BFP" >= 22.608575210962353 AND "players"."weight" >= 203.3128481653234 AND "awardsplayers"."yearID" >= 2004 AND "awardsshareplayers"."playerID" IS NOT NULL AND "battingpost"."playerID" IS NOT NULL AND "fielding"."playerID" IS NOT NULL;