
SELECT SUM("teams"."G") as agg_0 FROM "allstarfull" LEFT OUTER JOIN "teams" ON "allstarfull"."teamID" = "teams"."teamID" LEFT OUTER JOIN "els_teamnames" ON "teams"."teamID" = "els_teamnames"."teamid" LEFT OUTER JOIN "teamsfranchises" ON "els_teamnames"."franchid" = "teamsfranchises"."franchID" LEFT OUTER JOIN "teamshalf" ON "teams"."teamID" = "teamshalf"."teamID"  WHERE "allstarfull"."gameID" NOT LIKE '%ALS1%99707080%' AND "teamshalf"."teamID" NOT LIKE '%PHI%' AND "teams"."park" IS NULL AND "teamsfranchises"."franchName" LIKE '%Louisvi%lle%' AND "els_teamnames"."lgid" LIKE '%NL%';