
SELECT COUNT(*) as agg_0 FROM "appearances" LEFT OUTER JOIN "players" ON "appearances"."playerID" = "players"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "fielding" ON "players"."playerID" = "fielding"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID"  WHERE "appearances"."yearID" BETWEEN 1936 AND 1977 AND ("fielding"."lgID" = '%FL%' OR "fielding"."lgID" NOT LIKE '%N%L%' OR "fielding"."lgID" LIKE '%AA%') AND "awardsplayers"."lgID" LIKE '%A%L%' AND "fieldingpost"."POS" NOT LIKE '%CF%' AND "batting"."CS" <= 0.0 AND "appearances"."G_all" <= 22 AND "awardsplayers"."playerID" IN ('willite01', 'bondsba01', 'rodrial01', 'schmimi01', 'musiast01', 'mayswi01', 'dimagjo01') AND "pitchingpost"."playerID" IS NOT NULL;