
SELECT MAX("teams"."SOA") as agg_0 FROM "managershalf" LEFT OUTER JOIN "managers" ON "managershalf"."managerID" = "managers"."managerID" LEFT OUTER JOIN "awardsmanagers" ON "managers"."managerID" = "awardsmanagers"."managerID" LEFT OUTER JOIN "teams" ON "managers"."teamID" = "teams"."teamID" LEFT OUTER JOIN "awardssharemanagers" ON "managers"."managerID" = "awardssharemanagers"."managerID" LEFT OUTER JOIN "els_teamnames" ON "teams"."teamID" = "els_teamnames"."teamid"  WHERE "awardsmanagers"."lgID" LIKE '%NL%' AND "managershalf"."lgID" LIKE '%AL%' AND "awardssharemanagers"."managerID" IS NOT NULL AND "els_teamnames"."teamid" IS NOT NULL;