
SELECT MIN("teams"."BBA") as agg_0 FROM "awardssharemanagers" LEFT OUTER JOIN "managers" ON "awardssharemanagers"."managerID" = "managers"."managerID" LEFT OUTER JOIN "teams" ON "managers"."teamID" = "teams"."teamID" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner" LEFT OUTER JOIN "managershalf" ON "managers"."managerID" = "managershalf"."managerID"  WHERE ("managershalf"."teamID" != '%ML4%' OR "managershalf"."teamID" LIKE '%T%OR%' OR "managershalf"."teamID" IN ('CAL', 'PIT', 'SEA', 'PHI', 'CIN', 'BLN', 'LS3', 'NYA', 'SLN')) AND "seriespost"."teamIDwinner" IN ('LAN') AND ("managers"."managerID" NOT LIKE '%mcgrajo01%m%' OR "managers"."lgID" IN ('AA', 'NL') OR "managers"."managerID" IN ('mcgrajo01m', 'larusto01m', 'mauchge01m')) AND "awardssharemanagers"."managerID" LIKE '%garneph01m%' AND "managershalf"."lgID" LIKE '%N%L%';