
SELECT MIN("awardsplayers"."yearID") as agg_0 FROM "halloffame" LEFT OUTER JOIN "players" ON "halloffame"."hofID" = "players"."hofID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "schools" ON "schoolsplayers"."schoolID" = "schools"."schoolID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID" LEFT OUTER JOIN "pitching" ON "players"."playerID" = "pitching"."playerID"  WHERE "fieldingpost"."lgID" LIKE '%N%L%' AND ("fieldingpost"."PO" BETWEEN 0 AND 2 OR "fieldingpost"."teamID" LIKE '%CHN%') AND "awardsshareplayers"."lgID" LIKE '%N%L%' AND "batting"."playerID" IS NOT NULL AND "fieldingof"."playerID" IS NOT NULL AND "schools"."schoolID" IS NOT NULL AND "pitchingpost"."playerID" IS NOT NULL AND "pitching"."playerID" IS NOT NULL;