
SELECT MIN("appearances"."G_dh") as agg_0 FROM "allstarfull" LEFT OUTER JOIN "teams" ON "allstarfull"."teamID" = "teams"."teamID" LEFT OUTER JOIN "batting" ON "teams"."teamID" = "batting"."teamID" LEFT OUTER JOIN "players" ON "batting"."playerID" = "players"."playerID" LEFT OUTER JOIN "teamshalf" ON "teams"."teamID" = "teamshalf"."teamID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID"  WHERE "appearances"."lgID" LIKE '%AA%' AND "allstarfull"."GP" <= 1.0 AND "players"."debut" != '%7/23/1917%' AND "teamshalf"."teamID" IS NOT NULL AND "halloffame"."hofID" IS NOT NULL;