
SELECT MAX("fieldingpost"."DP") as agg_0 FROM "pitchingpost" LEFT OUTER JOIN "players" ON "pitchingpost"."playerID" = "players"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID" LEFT OUTER JOIN "teams" ON "batting"."teamID" = "teams"."teamID" LEFT OUTER JOIN "schools" ON "schoolsplayers"."schoolID" = "schools"."schoolID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID"  WHERE "schools"."schoolCity" LIKE '%S%an%' AND "teams"."franchID" LIKE '%LAD%' AND "pitchingpost"."ER" BETWEEN 0 AND 3 AND "pitchingpost"."R" != 0 AND "awardsshareplayers"."playerID" IS NOT NULL;