
SELECT MAX("players"."birthDay") as agg_0 FROM "awardsshareplayers" LEFT OUTER JOIN "players" ON "awardsshareplayers"."playerID" = "players"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID"  WHERE "players"."birthState" NOT LIKE '%M%D%' AND "players"."birthCity" LIKE '%Francisco%' AND "awardsshareplayers"."lgID" LIKE '%NL%' AND "schoolsplayers"."schoolID" NOT LIKE '%holycros%s%' AND "awardsshareplayers"."votesFirst" BETWEEN 0.0 AND 1.0 AND "players"."college" = '%York%' AND "players"."deathCity" != '%Rivera%' AND "awardsshareplayers"."awardID" IN ('MVP') AND "fieldingof"."playerID" IS NOT NULL;