
SELECT COUNT(*) as agg_0 FROM "fielding" LEFT OUTER JOIN "players" ON "fielding"."playerID" = "players"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID"  WHERE ("battingpost"."R" >= 2 OR "battingpost"."BB" BETWEEN 0 AND 1) AND ("halloffame"."category" != '%Pioneer/Executive%' OR ("halloffame"."hofID" = '%mcauldi01h%' AND "halloffame"."category" NOT LIKE '%Player%')) AND "schoolsplayers"."playerID" IS NOT NULL AND "awardsplayers"."playerID" IS NOT NULL AND "batting"."playerID" IS NOT NULL AND "appearances"."playerID" IS NOT NULL;