
SELECT SUM("batting"."G") as agg_0 FROM "pitchingpost" LEFT OUTER JOIN "players" ON "pitchingpost"."playerID" = "players"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID"  WHERE "appearances"."lgID" IN ('PL', 'AA', 'UA', 'FL', 'AL') AND "players"."deathState" LIKE '%PA%' AND "appearances"."G_ss" <= 0 AND ("awardsplayers"."awardID" LIKE '%All-Star%' OR "awardsplayers"."notes" = '%LHP%') AND "awardsshareplayers"."playerID" IS NOT NULL;