
SELECT AVG("teams"."BPF") as agg_0 FROM "els_teamnames" LEFT OUTER JOIN "teams" ON "els_teamnames"."teamid" = "teams"."teamID" LEFT OUTER JOIN "managers" ON "teams"."teamID" = "managers"."teamID" LEFT OUTER JOIN "allstarfull" ON "teams"."teamID" = "allstarfull"."teamID" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner" LEFT OUTER JOIN "teamsfranchises" ON "els_teamnames"."franchid" = "teamsfranchises"."franchID"  WHERE "managers"."teamID" IN ('BOS', 'NYA', 'CHA', 'PHI', 'PIT', 'SLN', 'CLE', 'CIN', 'DET') AND "managers"."teamID" NOT LIKE '%MON%' AND "teamsfranchises"."franchID" IN ('PBS', 'MIL') AND "teams"."Rank" BETWEEN 1 AND 7 AND "managers"."rank" >= 1.0 AND "allstarfull"."teamID" IS NOT NULL AND "seriespost"."teamIDwinner" IS NOT NULL;