
SELECT MIN("appearances"."G_batting") as agg_0 FROM "awardsplayers" LEFT OUTER JOIN "players" ON "awardsplayers"."playerID" = "players"."playerID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "pitching" ON "players"."playerID" = "pitching"."playerID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID" LEFT OUTER JOIN "fielding" ON "players"."playerID" = "fielding"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID"  WHERE "appearances"."teamID" NOT LIKE '%C%IN%' AND "halloffame"."category" NOT LIKE '%Pioneer/Executiv%e%' AND "awardsshareplayers"."lgID" = '%AL%' AND "battingpost"."RBI" = 0 AND "appearances"."lgID" = '%AL%' AND "pitchingpost"."SH" <= 0.0 AND "pitching"."playerID" IS NOT NULL AND "fielding"."playerID" IS NOT NULL AND "batting"."playerID" IS NOT NULL;