
SELECT SUM("teams"."CG") as agg_0 FROM "pitchingpost" LEFT OUTER JOIN "players" ON "pitchingpost"."playerID" = "players"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID" LEFT OUTER JOIN "teams" ON "batting"."teamID" = "teams"."teamID" LEFT OUTER JOIN "teamshalf" ON "teams"."teamID" = "teamshalf"."teamID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID"  WHERE "teams"."teamID" IN ('NYA', 'SLN', 'PIT', 'DET') AND "batting"."SH" >= 0.0 AND "teams"."H" >= 1183 AND "teams"."CS" BETWEEN 18.319872806334534 AND 30.007281705259416 AND "players"."height" >= 66.1831077736542 AND "teamshalf"."teamID" IS NOT NULL AND "awardsplayers"."playerID" IS NOT NULL AND "appearances"."playerID" IS NOT NULL;