
SELECT AVG("schoolsplayers"."yearMax") as agg_0 FROM "pitching" LEFT OUTER JOIN "players" ON "pitching"."playerID" = "players"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID"  WHERE "players"."bats" = '%L%' AND "fieldingpost"."lgID" IN ('AL') AND "battingpost"."teamID" LIKE '%SLN%' AND "battingpost"."round" LIKE '%W%S%' AND ("schoolsplayers"."yearMin" <= 1991 OR ("schoolsplayers"."yearMin" <= 2002 AND "schoolsplayers"."yearMin" <= 1928)) AND "battingpost"."IBB" >= 0.0 AND "halloffame"."hofID" IS NOT NULL AND "fieldingof"."playerID" IS NOT NULL AND "appearances"."playerID" IS NOT NULL;