
SELECT SUM("teams"."IPouts") as agg_0 FROM "teamshalf" LEFT OUTER JOIN "teams" ON "teamshalf"."teamID" = "teams"."teamID" LEFT OUTER JOIN "salaries" ON "teams"."teamID" = "salaries"."teamID" LEFT OUTER JOIN "batting" ON "teams"."teamID" = "batting"."teamID" LEFT OUTER JOIN "allstarfull" ON "teams"."teamID" = "allstarfull"."teamID" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner"  WHERE "salaries"."teamID" NOT LIKE '%TB%A%' AND "batting"."yearID" <= 1931 AND ("salaries"."teamID" NOT LIKE '%BOS%' OR ("salaries"."yearID" != 1998 AND "salaries"."teamID" != '%ARI%')) AND "allstarfull"."teamID" IS NOT NULL AND "seriespost"."teamIDwinner" IS NOT NULL;