
SELECT MAX("managershalf"."yearID") as agg_0 FROM "managershalf" LEFT OUTER JOIN "managers" ON "managershalf"."managerID" = "managers"."managerID" LEFT OUTER JOIN "awardssharemanagers" ON "managers"."managerID" = "awardssharemanagers"."managerID" LEFT OUTER JOIN "awardsmanagers" ON "managers"."managerID" = "awardsmanagers"."managerID" LEFT OUTER JOIN "teams" ON "managers"."teamID" = "teams"."teamID" LEFT OUTER JOIN "teamshalf" ON "teams"."teamID" = "teamshalf"."teamID" LEFT OUTER JOIN "allstarfull" ON "teams"."teamID" = "allstarfull"."teamID"  WHERE "managershalf"."managerID" NOT LIKE '%andersp01m%' AND "allstarfull"."gameID" NOT LIKE '%NLS1988%07120%' AND "awardssharemanagers"."pointsMax" >= 140 AND "managers"."lgID" = '%AL%' AND ("managershalf"."teamID" IN ('LS3', 'CAL', 'BLN', 'SEA', 'CIN', 'CHN', 'SLN', 'PIT', 'PHI') OR "managershalf"."W" <= 28) AND "awardssharemanagers"."lgID" = '%AL%' AND "managershalf"."inseason" <= 1 AND "awardsmanagers"."managerID" IS NOT NULL AND "teamshalf"."teamID" IS NOT NULL;