
SELECT MIN("battingpost"."2B") as agg_0 FROM "schools" LEFT OUTER JOIN "schoolsplayers" ON "schools"."schoolID" = "schoolsplayers"."schoolID" LEFT OUTER JOIN "players" ON "schoolsplayers"."playerID" = "players"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID"  WHERE "halloffame"."inducted" = '%Y%' AND "appearances"."teamID" LIKE '%SLA%' AND ("batting"."RBI" >= 1.197901001284213 OR "batting"."SB" >= 1.333878650836501) AND "schools"."schoolState" LIKE '%K%S%' AND "fieldingpost"."G" BETWEEN 1 AND 6 AND "pitchingpost"."playerID" IS NOT NULL;