
SELECT SUM("fieldingof"."stint") as agg_0 FROM "fieldingof" LEFT OUTER JOIN "players" ON "fieldingof"."playerID" = "players"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID"  WHERE "awardsplayers"."awardID" IN ('Baseball Magazine All-Star', 'TSN All-Star', 'Most Valuable Player', 'TSN Player of the Year', 'Rookie of the Year', 'Gold Glove', 'Cy Young Award', 'Silver Slugger') AND ("fieldingof"."playerID" = '%muncejo01%' OR "fieldingof"."Glf" BETWEEN 0.0 AND 1.7869370616123574 OR "fieldingof"."playerID" = '%mannier01%') AND "awardsplayers"."notes" NOT LIKE '%D%H%' AND "players"."nameFirst" IN ('George', 'Jim', 'Bill', 'Mike', 'Frank', 'Tom', 'Ed', 'John', 'Bob') AND "players"."throws" IN ('R') AND "awardsplayers"."lgID" LIKE '%N%L%';