
SELECT COUNT(*) as agg_0 FROM "awardssharemanagers" LEFT OUTER JOIN "managers" ON "awardssharemanagers"."managerID" = "managers"."managerID" LEFT OUTER JOIN "teams" ON "managers"."teamID" = "teams"."teamID"  WHERE "awardssharemanagers"."lgID" NOT LIKE '%NL%' AND "awardssharemanagers"."awardID" IN ('Mgr of the year') AND "managers"."managerID" NOT LIKE '%mac%kco01m%' AND ("teams"."Rank" BETWEEN 3 AND 10 OR ("teams"."HRA" BETWEEN 10 AND 235 AND "teams"."Rank" <= 6)) AND ("awardssharemanagers"."managerID" NOT LIKE '%mckeoja99m%' OR "awardssharemanagers"."managerID" LIKE '%howear01m%' OR "awardssharemanagers"."managerID" NOT LIKE '%manuech0%1m%');