
SELECT AVG("teams"."R") as agg_0 FROM "managershalf" LEFT OUTER JOIN "managers" ON "managershalf"."managerID" = "managers"."managerID" LEFT OUTER JOIN "teams" ON "managers"."teamID" = "teams"."teamID" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner" LEFT OUTER JOIN "teamshalf" ON "teams"."teamID" = "teamshalf"."teamID" LEFT OUTER JOIN "awardssharemanagers" ON "managers"."managerID" = "awardssharemanagers"."managerID" LEFT OUTER JOIN "allstarfull" ON "teams"."teamID" = "allstarfull"."teamID" LEFT OUTER JOIN "els_teamnames" ON "teams"."teamID" = "els_teamnames"."teamid" LEFT OUTER JOIN "awardsmanagers" ON "managers"."managerID" = "awardsmanagers"."managerID"  WHERE "teams"."Rank" <= 4 AND "allstarfull"."gameID" LIKE '%ALS198307060%' AND "managershalf"."managerID" != '%powerpa99m%' AND "teamshalf"."lgID" LIKE '%AL%' AND "seriespost"."teamIDwinner" IS NOT NULL AND "awardssharemanagers"."managerID" IS NOT NULL AND "els_teamnames"."teamid" IS NOT NULL AND "awardsmanagers"."managerID" IS NOT NULL;