
SELECT MIN("fielding"."yearID") as agg_0 FROM "players" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "pitching" ON "players"."playerID" = "pitching"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID" LEFT OUTER JOIN "fielding" ON "players"."playerID" = "fielding"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID"  WHERE "awardsshareplayers"."awardID" NOT LIKE '%Year%' AND "appearances"."G_ss" <= 0 AND ("appearances"."G_2b" <= 0 OR "appearances"."G_rf" >= 40) AND "schoolsplayers"."playerID" IS NOT NULL AND "awardsplayers"."playerID" IS NOT NULL AND "pitchingpost"."playerID" IS NOT NULL AND "pitching"."playerID" IS NOT NULL AND "fieldingof"."playerID" IS NOT NULL AND "battingpost"."playerID" IS NOT NULL AND "halloffame"."hofID" IS NOT NULL;