SELECT SUM("players_teams"."PF") as agg_0, MAX("teams"."o_fgm" + "teams"."divLoss") as agg_1 FROM "awards_players" LEFT OUTER JOIN "players" ON "awards_players"."playerID" = "players"."playerID" LEFT OUTER JOIN "players_teams" ON "players"."playerID" = "players_teams"."playerID" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID"  WHERE "players_teams"."lgID" NOT LIKE '%NBA%' AND "players"."birthCountry" NOT LIKE '%US%A%' AND "players"."playerID" >= 165083 AND "teams"."arena" = 'Fair Grounds Pavilion' AND "players_teams"."PostthreeMade" = 51