SELECT MIN("teams"."o_oreb" + "teams"."neutLoss") as agg_0, MIN("draft"."draftSelection" + "teams"."lost") as agg_1 FROM "awards_coaches" LEFT OUTER JOIN "coaches" ON "awards_coaches"."coachID" = "coaches"."coachID" LEFT OUTER JOIN "teams" ON "coaches"."tmID" = "teams"."tmID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDLoser" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID"  WHERE "draft"."firstName" LIKE '%Jim%' AND ("series_post"."lgIDLoser" NOT LIKE '%NB%A%' OR "series_post"."lgIDLoser" = 'ABA')