SELECT MAX("teams"."pace" + "teams"."d_tmRebound") as agg_0, SUM("series_post"."tmIDLoser") as agg_1, COUNT(*) as agg_2 FROM "series_post" LEFT OUTER JOIN "teams" ON "series_post"."tmIDLoser" = "teams"."tmID" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID" LEFT OUTER JOIN "awards_coaches" ON "coaches"."coachID" = "awards_coaches"."coachID"  WHERE "awards_coaches"."id" <= 15 AND "teams"."d_to" BETWEEN 294 AND 1539