SELECT AVG("teams"."confRank") as agg_0 FROM "players" LEFT OUTER JOIN "awards_players" ON "players"."playerID" = "awards_players"."playerID" LEFT OUTER JOIN "player_allstar" ON "players"."playerID" = "player_allstar"."playerID" LEFT OUTER JOIN "players_teams" ON "players"."playerID" = "players_teams"."playerID" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID"  WHERE "players"."college" IN ('Duke', 'St. John's (NY)', 'UCLA', 'Indiana', 'North Carolina', 'Kentucky', 'none', 'Notre Dame') AND "players"."nameSuffix" IN ('IV') AND "awards_players"."award" IN ('All-NBA Third Team', 'All-ABA First Team', 'All-Defensive Second Team', 'All-Defensive First Team', 'Most Valuable Player', 'All-NBA Second Team', 'Finals MVP', 'Rookie of the Year', 'All-NBA First Team') AND "players"."highSchool" = 'Blackburg' AND "players"."birthCity" NOT LIKE '%Yo%rk%'