SELECT SUM("players_teams"."assists" + "teams"."o_tmRebound") as agg_0, MAX("teams"."year") as agg_1 FROM "players_teams" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID"  WHERE ("players_teams"."lgID" = 'ABL1' OR "players_teams"."lgID" NOT LIKE '%A%BA%' OR "players_teams"."lgID" NOT LIKE '%AB%A%') AND "teams"."arena" LIKE '%HemisF%air%' AND "teams"."divID" NOT LIKE '%A%T%' AND "teams"."confRank" <= 0 AND "teams"."lgID" LIKE '%A%BA%'