SELECT MIN("teams"."d_stl" + "teams"."o_3pm") as agg_0, AVG("awards_players"."playerID" + "players_teams"."PostBlocks") as agg_1 FROM "awards_players" LEFT OUTER JOIN "players" ON "awards_players"."playerID" = "players"."playerID" LEFT OUTER JOIN "players_teams" ON "players"."playerID" = "players_teams"."playerID" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID"  WHERE "players_teams"."lgID" != 'NBL' AND "teams"."attendance" != 0