SELECT AVG("teams"."d_pts" + "teams"."divWon") as agg_0, AVG("teams"."games" + "teams"."d_oreb") as agg_1 FROM "awards_coaches" LEFT OUTER JOIN "coaches" ON "awards_coaches"."coachID" = "coaches"."coachID" LEFT OUTER JOIN "teams" ON "coaches"."tmID" = "teams"."tmID"  WHERE "coaches"."lost" BETWEEN 23.793086390604785 AND 52.872353207713594 AND "coaches"."post_losses" BETWEEN 0.0 AND 1.0