SELECT COUNT(*) as agg_0 FROM "teams" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDLoser" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID" LEFT OUTER JOIN "players_teams" ON "teams"."tmID" = "players_teams"."tmID"  WHERE "teams"."bbtmID" NOT LIKE '%GSW%' AND ("players_teams"."lgID" NOT LIKE '%ABA%' OR "players_teams"."stint" <= 1 OR "players_teams"."lgID" IN ('ABL1', 'NBL', 'NBA', 'NPBL'))