SELECT MAX("players"."weight" + "awards_players"."playerID") as agg_0 FROM "awards_players" LEFT OUTER JOIN "players" ON "awards_players"."playerID" = "players"."playerID"  WHERE "players"."hsCity" NOT LIKE '%Philadelp%hia%' AND "awards_players"."lgID" NOT LIKE '%NB%A%' AND ("awards_players"."award" LIKE '%Year%' OR "awards_players"."playerID" >= 216635 OR "awards_players"."award" IN ('Most Valuable Player', 'All-ABA Second Team')) AND "awards_players"."note" LIKE '%ti%e%'